import React from 'react';
import ImageGallery from 'react-image-gallery';
import Carousel, { Modal, ModalGateway } from "react-images";
import { createSearchParams, useLocation, useNavigate } from 'react-router-dom';
import Gallery from 'react-photo-gallery'

import { Painting } from './types';

interface PhotoGalleryProps {
  paintings: Painting[];
}

export default function PhotoGallery(props: PhotoGalleryProps) {
  const { paintings } = props;

  const location = useLocation();
  const navigate = useNavigate();

  const params = new URLSearchParams(location.search);
  const selectedPhotoId = params.get('selected');
  const selectedPhotoIdx = selectedPhotoId ? paintings.findIndex((p) => p.id === parseInt(selectedPhotoId)) : undefined;

  const photos = paintings.map((p) => {
    return {
      src: p.photo.url,
      width: p.photo.width,
      height: p.photo.height,
      source: p.photo.url,
      caption: p.name,
    };
  });

  return (
    <div>
      <Gallery
        photos={photos}
        onClick={() => {
          navigate({
              pathname: location.pathname,
              search: createSearchParams({
                  foo: "bar"
              }).toString()
          });
        }}
      />
      {
        selectedPhotoIdx !== undefined && (
          <ModalGateway>
            <Modal onClose={() => {}}>
              <Carousel
                currentIndex={selectedPhotoIdx}
                views={photos}
              />
            </Modal>
        </ModalGateway>
        )
      }

    </div>
  )
}
