import React from 'react';
import { Sidebar, Menu, MenuItem } from 'react-pro-sidebar';
import './App.css';
import { QueryClient, QueryClientProvider } from '@tanstack/react-query';
import PhotoGallery from './PhotoGallery';
import { Link, NavLink, Navigate, Outlet, Route, Routes } from 'react-router-dom';
import AboutJamesGordaneer from './AboutJamesGordaneer';
import HowToAdoptAPainting from './HowToAdoptAPainting';

const queryClient = new QueryClient();

function AppInner() {
  return (
    <div>
      <div className="App flex flex-col justify-center h-max">
        <header className="App-header">
          Paintings by Jim Gordaneer
        </header>
        {/* <Sidebar>
          <Menu
            menuItemStyles={{
              button: {
                // the active class will be added automatically by react router
                // so we can use it to style the active menu item
                [`&.active`]: {
                  backgroundColor: '#13395e',
                  color: '#b6c8d9',
                },
              },
            }}
          >
            <MenuItem component={<Link to="/gallery" />}> Gallery</MenuItem>
            <MenuItem component={<Link to="/about" />}> About James Gordaneer</MenuItem>
            <MenuItem component={<Link to="/adopt" />}> Adopt a Painting</MenuItem>
            <MenuItem component={<Link to="/e-commerce" />}> E-commerce</MenuItem>
          </Menu>
        </Sidebar>
        <Routes>
          <Route path="/" element={<Navigate replace to="/gallery" />} />
          <Route path="gallery" element={<PhotoGallery />} />
          <Route path="about" element={<AboutJamesGordaneer />} />
          <Route path="adopt" element={<HowToAdoptAPainting />} />
          <Route path="*" element={<Navigate replace to="/gallery" />} />
        </Routes> */}
          {/* Routes nest inside one another. Nested route paths build upon
        parent route paths, and nested route elements render inside
        parent route elements. See the note about <Outlet> below. */}
          <Routes>
            <Route path="/" element={<Layout />}>
              <Route index element={<PhotoGallery />} />
              <Route path="about" element={<AboutJamesGordaneer />} />
              <Route path="adopt" element={<HowToAdoptAPainting />} />
            </Route>
          </Routes>
      </div>
    </div>
  );
}

function Layout() {
  // return (
  //   <div>
  //   {/* A "layout route" is a good place to put markup you want to
  //   share across all the pages on your site, like navigation. */}
  //     <Sidebar>
  //       <Menu
  //         menuItemStyles={{
  //           button: {
  //             // the active class will be added automatically by react router
  //             // so we can use it to style the active menu item
  //             [`&.active`]: {
  //               backgroundColor: '#13395e',
  //               color: '#b6c8d9',
  //             },
  //           },
  //         }}
  //       >
  //         <MenuItem component={<NavLink to="/gallery" />}> Gallery</MenuItem>
  //         <MenuItem component={<NavLink to="/about" />}> About James Gordaneer</MenuItem>
  //         <MenuItem component={<NavLink to="/adopt" />}> Adopt a Painting</MenuItem>
  //       </Menu>
  //     </Sidebar>
  //     {/* An <Outlet> renders whatever child route is currently active,
  //         so you can think about this <Outlet> as a placeholder for
  //         the child routes we defined above. */}
  //     <Outlet />
  //   </div>
  // );
  return (
    <div>
      <nav>
        <ul>
          <li>
            <Link to="/">Gallery</Link>
          </li>
          <li>
            <Link to="/about">About</Link>
          </li>
        </ul>
      </nav>

      <hr />

      <Outlet />
    </div>
  );
}

function App() {
  return (
    <QueryClientProvider client={queryClient}>
      <AppInner />
    </QueryClientProvider>
  );
}

export default App;
